import logo from './logo.svg';
import './App.css';

function App() {
  const student=['Monir','Alim','raju','karim','Raj']
  const products=[{name:'Iphone', price:'$1000'},
                  {name:'Vivo', price:'$100'},
                  {name:'xami', price:'$999'}
]
  return (
    <div className="App">
      <header className="App-header">
       <h1>I am React Developer</h1>
       <ul>
        {
          student.map(student =><li>{student}</li>)
        }
        <li>{student[0]}</li>
       </ul>
       <Product product={products[0]}></Product><br></br>
       <Product product={products[1]}></Product><br></br>
       <Product product={products[2]}></Product><br></br>

       <Person name="Abdullah" cgpa="4" Id="1223455"></Person>
       <Person name="Alim" cgpa="3" Id="122244"></Person>
       <Person name="Rakib" cgpa="3.5" Id="134455"></Person>
        <Person name="Abdullah" cgpa="4" Id="1223455"></Person>
       <Person name="Alim" cgpa="3" Id="122244"></Person>
       <Person name="Rakib" cgpa="3.5" Id="134455"></Person>
      </header>
    </div>
  );
}
function Product(props){
  const productStyle={
    border:'1px solid gray',
    borderRadius:'5px',
    backgroundColor:'purple ',
    height:'200px',
    width:'200px',
    float:'left',
  }
  const {name,price}=props.product;
  console.log(name,price)

  return(
    <div style={productStyle}>
      <h2>{name}</h2>
      <h3>{price}</h3>
      <button>Buy Now</button>
    </div>
  )

}
function Person(props){
  const personStyle={
    border:'2px solid green',
    margin:'12px',
    padding:'10px',
    width:'400px'
  }
  return(
    <div style={personStyle}>
      <h2>Name:{props.name}</h2>
      <h3>cgpa:{props.cgpa}</h3>
      <h3>Id:{props.Id}</h3>


    </div>

  )
}

export default App;
